import axios from 'axios';
import {
  all,
  call,
  fork,
  put,
  take,
  takeLatest,
  select,
} from 'redux-saga/effects';
import { GetCityFailed, GetCitySuccess } from '../actions/actions';
import {ACTION_TYPES} from '../constant/actionTypes';
import {AddressUrl} from '../constant/addressUrl';
const getAddressData = (params: any) => {
  axios.get(AddressUrl.Province, {params})
  .then(response => {
    console.log(response);



  });
};
function* fetchCity(action: any) {
  try {
    //Chỗ này gọi api rồi trả về cho reducer

    const data = yield call(getAddressData, params)
   if(data.data){
     yield put(GetCitySuccess(data.data))
   }
  } catch (error) {
    console.log(error)
    yield put(GetCityFailed(error));
  } finally {
  }
}

function* listen() {
  yield takeLatest(ACTION_TYPES.GET_CITY, fetchCity);
}

export default function* () {
  yield all([listen()]);
}
