import React from "react";
import { FlatList, View, Image,Text } from "react-native";
import { colors,fonts } from "../../styles/typography";


const array1=[{
 img: require('../../assets/image/icon/tao_don.png') ,title:"Tạo đơn "},
{img: require('../../assets/image/icon/don_hang.png'),title: "Đơn hàng"},
{img: require('../../assets/image/icon/tien_hang.png'), title: 'Tiền hàng'},
{img: require('../../assets/image/icon/tim_bank.png'), title: 'Tìm BC'}]

const FlatListHome=()=>{

const renderItem=({item, index})=>{
    return(
        <View style={{alignItems: 'center'}}>
         <Image source={item.img} resizeMode="contain"/>
         <Text>{item.title}</Text>
        </View>
    )
}



    return(
<View style={{flex:1, backgroundColor: colors.white}}>
<FlatList 
       data={array1}
       renderItem={renderItem}
       showsHorizontalScrollIndicator={false}
       showsVerticalScrollIndicator={false}
       horizontal={true}/>
</View>
    )
}

export default FlatListHome
