import React from 'react';
import {
  Dimensions,
  Image,
  ImageBackground,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from 'react-native';
import {colors, fonts, fontSize} from '../../styles/typography';



const {width, height} = Dimensions.get('screen');
const HeaderFlatlist = () => {
    const [visiable, setVisiable] = useState<boolean>(true);
    const [placehold, setPlacehold] = useState<string>('Tìm kiếm')
    const onBlur = () => {
        setVisiable(true);
        setPlacehold('Tìm kiếm');
      };
      const onFocus = () => {
        setVisiable(false);
        setPlacehold('');
      };


      return(
        <View style={{flex: 1, backgroundColor: colors.white}}>
        <ImageBackground
          source={require('../../assets/image/img/background.png')}
          resizeMode="stretch"
          style={{height: 200, alignItems: 'center', paddingHorizontal: 18}}>
          <View
            style={{
              flexDirection: 'row',
              marginTop: 60,
              alignItems: 'center',
              justifyContent: 'space-between',
            }}>
            <Image
              source={require('../../assets/image/icon/qrScan.png')}
              resizeMode="contain"
            />
            <View style={{flex: 1}}></View>
            <View
              style={{
                flexDirection: 'row',
                backgroundColor: colors.white,
                borderRadius: 4,
                width: '90%',
                alignItems: 'center',
              }}>
              {visiable && (
                <Image
                  source={require('../../assets/image/icon/search.png')}
                  resizeMode="contain"
                />
              )}
              <TextInput
                onBlur={onBlur}
                onFocus={onFocus}
                placeholder={placehold}
                style={{padding: 0}}
              />
            </View>
          </View>
          <View
            style={{
              flexDirection: 'row',
              width: '100%',
              justifyContent: 'space-between',
              borderRadius: 10,
              borderColor: colors.background,
              backgroundColor: colors.white,
              borderWidth: 0.5,
              height: 60,
              marginHorizontal: 18,
              position: 'absolute',
              bottom: -50,
            }}>
            <View
              style={{
                flexDirection: 'row',
                justifyContent: 'space-between',
                width: width * 0.4,
                alignItems: 'center',
                paddingHorizontal: 10,
              }}>
              <View style={{justifyContent: 'center'}}>
                <Text
                  style={{
                    fontFamily: fonts.normal,
                    fontSize: fontSize[14],
                    color: colors.gray,
                  }}>
                  Điểm thưởng
                </Text>
                <View
                  style={{
                    flexDirection: 'row',
                    alignItems: 'center',
                  }}>
                  <Image
                    source={require('../../assets/image/icon/crown.png')}
                    resizeMode="contain"
                  />
                  <Text
                    style={{
                      fontFamily: fonts.bold,
                      fontSize: fontSize[14],
                      fontWeight: 'bold',
                    }}>
                    {' '}
                    0 điểm
                  </Text>
                </View>
              </View>
  
              <Image
                source={require('../../assets/image/icon/next.png')}
                resizeMode="contain"
              />
            </View>
            <View
              style={{
                width: 0.5,
                backgroundColor: colors.gray,
                height: '100%',
              }}></View>
            <View
              style={{
                flexDirection: 'row',
                alignItems: 'center',
                justifyContent: 'space-between',
                width: width * 0.4,
                paddingHorizontal: 10,
              }}>
              <View
                style={{
                  justifyContent: 'center',
                }}>
                <Text
                  style={{
                    fontFamily: fonts.normal,
                    fontSize: fontSize[14],
                    color: colors.gray,
                  }}>
                  Tài khoản
                </Text>
                <View
                  style={{
                    flexDirection: 'row',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                  }}>
                  <Image
                    source={require('../../assets/image/icon/wallet.png')}
                    resizeMode="contain"
                  />
                  <Text
                    style={{
                      fontFamily: fonts.bold,
                      fontSize: fontSize[14],
                      fontWeight: 'bold',
                    }}>
                    {' '}
                    88.000.000 VNĐ
                  </Text>
                </View>
              </View>
  
              <Image
                source={require('../../assets/image/icon/next.png')}
                resizeMode="contain"
              />
            </View>
          </View>
        </ImageBackground>
      )
};
export default HeaderFlatlist;
